import React from 'react';
import PropTypes from 'prop-types';
// import { a } from 'react-router-dom';


export default function Navbar(props) {
  return (
    <nav className={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
      <div className="container-fluid">
        <a className="navbar-brand" href="/">{props.title}</a>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav">
            <li className="nav-item"  >
              <a className="nav-a mx-3" style={{textDecoration:'none',}} href="/">Home</a>
              <a className="nav-a mx-3" style={{textDecoration:'none'}} href="/about">About</a>
            </li>
          </ul>
        </div>
        <div className={`form-check form-switch text-${props.mode === 'light' ? 'dark' : 'light'}`}>
          <input className="form-check-input" type="checkbox" role="switch" id="switchCheckDefault" onClick={props.toggleMode} />
          <label className="form-check-label" htmlFor="switchCheckDefault" >Enable Dark Mode</label>
        </div>
      </div>
    </nav>
  );
}

Navbar.propTypes = {  // <-- lowercase 'p' here!
  title: PropTypes.string.isRequired,
  about: PropTypes.string.isRequired
};

Navbar.defaultProps = {
  title: `set title here`,
  about: `about text here`
};
