import React, { useState } from 'react';

export default function TextForm(props) {
    const [text, setText] = useState('');

    const handleUpClick = () => {
        let newText = text.toUpperCase();
        setText(newText);
        props.showAlert("Converted To Uppercase", "success")
    };

    const handleLoClick = () => {
        let newText = text.toLowerCase();
        setText(newText);
        props.showAlert("Converted To Lowercase", "success")
    };


    const handleCopy = () => {
        navigator.clipboard.writeText(text);
        props.showAlert("Copied to Clipboard!", "success");
    };


    const removeExtraSpaces = () => {
        let newText = text.trim().replace(/\s+/g, ' ');
        setText(newText);
        props.showAlert("Extra spaces removed!", "success");
    }




    const handleOnChange = (event) => {
        setText(event.target.value);

    };

    const clearButton = () => {
        setText('');
        props.showAlert("Text Cleared", "success")
    }

    // Count words properly
    const wordCount = text.trim() === '' ? 0 : text.trim().split(/\s+/).length;

    return (
        <>
            <div className="container" style={{ color: props.mode === 'light' ? '#031535' : 'white' }}>
                <h1 >{props.heading}</h1>
                <div className="mb-3 my-8">
                    <textarea
                        className="form-control"
                        value={text} style={{ backgroundColor: props.mode === 'dark' ? 'rgb(30 73 125)' : 'white', color: props.mode === 'dark' ? 'white' : 'rgb(30 73 125)' }}

                        onChange={handleOnChange}
                        id="mybox"
                        rows="8"
                    ></textarea>
                </div> 
                <button className="btn btn-primary mx-1 my-1 " onClick={handleUpClick}>
                    Convert To Uppercase
                </button>
                <button className="btn btn-primary mx-1 my-1" onClick={handleLoClick}>
                    Convert To Lowercase
                </button>
                <button className="btn btn-primary mx-1 my-1" onClick={clearButton}>
                    Clear Text</button>
                <button className="btn btn-primary mx-1 my-1" onClick={handleCopy}>
                    Copy Text
                </button>

                <button className="btn btn-primary mx-1" onClick={removeExtraSpaces}>
                    Remove Extra Spaces
                </button>

            </div>
            <div className="container my-2" style={{ color: props.mode === 'light' ? '#031535' : 'white' }}>
                <h1>Your text summary</h1>
                <p>{wordCount} words and {text.length} characters</p>
                <p>{0.0008 * wordCount} Minutes Read</p>
                <h2>Preview</h2>
                <p>{text.length > 0 ? text : 'Nothing to preview!'}</p>

            </div>
        </>
    );
}
